    const [checked, setChecked] = useState(false);
    //const [userInput, setUserInput] = useState('')

    // const changeInput = (event) => {
    //     setUserInput([event.currentTarget.value])
    // }


    const [statusInput, setStatusInput] = useState(0)
    const [userInput, setUserInput] = useState()

    const handleChange = (event) => {
        setUserInput(event.target.value)
        //console.log(event.target.value);
    }

    const changeStatusInput = (value, id) => {
        setUserInput(value)
        setStatusInput(1)
    }

    const saveChanges = () => {

    }
    const handleSubmit = (event) => {
        event.preventDefault()
        console.log('wow');
        //addTask(userInput)
        //setUserInput("")
    }

    const handleKeyPress = (event) => {

        if(event.key === "Enter") {
            console.log('this enter');
            handleSubmit(event)
        }

    }

    // function changeTask(event) {
    //     const taskCheck = ReactDOM.createRoot(document.getElementById(event.target.id));
    //     //setValue(event.target.textContent)
    //     //console.log(event.target.textContent);
    //     taskCheck.render (
    //         <form onSubmit={handleSubmit}>
    //            <input className="editTask" value={value} onChange={handleChange} type="text"/> 
    //         </form>
    //     )
    // }

    return (
        <div key={task.id} className="task">     
            <section className="task__left">
                <input className="task__check"  defaultChecked={task.check} onChange={() => setChecked(!checked)} onClick={() => checkTask(task)} type="checkbox" />
                { (statusInput === 1) ? (<input className="editTask" onKeyDown={(event)=>{handleKeyPress(event)}} value={userInput} onChange={handleChange} type="text"/> ):(<div className="task__text"id={task.id} onDoubleClick={()=>{changeStatusInput(task.title, task.id)}}>{task.title}</div>)}
            </section>
            <section className="task__right">
                <div className="task__date">{task.date}</div>
                <button className="task__btnDelete" onClick={() => removeTask(task.id)} >del</button>
            </section>
        </div>
    )
}

export default Task
