import React from "react";
import { useState, useEffect } from "react";

function Task({ task, removeTask, checkTask, tasks, validateInputTodo,checked, setChecked, userInput,setUserInput}) {

  const handleChange = (event) => {
    setUserInput(event.target.value);
  };

  const changeStatusInput = (value) => {
    setUserInput(value);
    setStatusInput(true);
    console.log(statusInput);
  };

  const blurInput = () => {
    setStatusInput(false);
  };

  useEffect(() => {
    console.log(statusInput);
  }, [statusInput]);


  const handleSubmit = (event, id) => {
    event.preventDefault();
    const title = String(userInput).trim();
    if (!validateInputTodo(title)) {
      setStatusInput(false);
      return;
    }
    if (title) {
      tasks.find((task) => {
        if (task.id === id) {
          task.title = title;
          return true;
        }
      });
    }

    setStatusInput(false);
  };

  const handleKeyPress = (event, id) => {
    switch (event.key) {
      case "Enter":
        handleSubmit(event, id);
        break;
      case "Escape":
        setStatusInput(false);
        break;
      default:
        break;
    }
  };

  return (
    <div key={task.id} className="task">
      <div className="task__inputs">
        <section className="task__left">
          <input
            className="task__check"
            defaultChecked={task.check}
            onChange={() => setChecked(!checked)}
            onClick={() => checkTask(task.id)}
            type="checkbox"
          />
          {statusInput ? (
            <input
              className="editTask"
              autoFocus
              onBlur={(event) => {
                blurInput(event, task.id);
              }}
              onKeyDown={(event) => {
                handleKeyPress(event, task.id);
              }}
              value={userInput}
              onChange={handleChange}
              type="text"
            />
          ) : (
            <div
              className="task__text"
              id={task.id}
              onDoubleClick={(event) => {
                changeStatusInput(task.title, event);
              }}
            >
              {task.title}
            </div>
          )}
        </section>
        <button className="task__btnDelete" onClick={() => removeTask(task.id)}>
          del
        </button>
      </div>

      <div className="task__date">{task.date}</div>
    </div>
  );
}

export default Task;
