import { useEffect } from "react";
import "./Filters.css";

function Filters({
  setFilter,
  setSort,
  setFiltredArray,
  setPagesCount,
  filters,
  tasks,
  sort,
  filtredArray,
}) {
  const onFilter = (filter) => {
    setFilter(filter);
  };

  const onSort = (sort) => {
    setSort(sort);
  };

  useEffect(() => {
    switch (filters) {
      case 0:
        setFiltredArray(
          [...tasks].sort((prev, next) =>
            sort ? prev.id - next.id : next.id - prev.id
          )
        );
        break;

      case 1:
        setFiltredArray(
          [...tasks]
            .filter((task) => task.check === true)
            .sort((prev, next) =>
              !sort  ? prev.id - next.id : next.id - prev.id
            )
        );
        break;

      default:
        setFiltredArray(
          [...tasks]
            .filter((task) => task.check !== true)
            .sort((prev, next) =>
              sort ? prev.id - next.id : next.id - prev.id
            )
        );
        break;
    }
  }, [filters, sort, tasks]);

  useEffect(() => {
    setPagesCount(Math.ceil(filtredArray.length / 4) || 1);
  }, [filtredArray, filters, sort]);

  return (
    <section className="filterBox">
      <div className="filterBox__filter">
        <button
          className={
            filters === "filter__all" ? "filter__all btnActive" : "filter__all"
          }
          onClick={() => {
            onFilter("filter__all");
          }}
        >
          All
        </button>
        <button
          className={
            filters === "filter__done"
              ? "filter__donel btnActive"
              : "filter__done"
          }
          onClick={() => {
            onFilter("filter__done");
          }}
        >
          Done
        </button>
        <button
          className={
            filters === "filter__undone"
              ? "filter__undone btnActive"
              : "filter__undone"
          }
          onClick={() => {
            onFilter("filter__undone");
          }}
        >
          Undone
        </button>
      </div>
      <div className="filterBox__sorting">
        <span>Sort by Date</span>
        <button
          className={
            sort === "sort__early" ? "sort__early btnActive" : "sort__early"
          }
          onClick={() => {
            onSort("sort__early");
          }}
        >
          Ear
        </button>
        <button
          className={
            sort === "sort__last" ? "sort__last btnActive" : "sort__last"
          }
          onClick={() => {
            onSort("sort__last");
          }}
        >
          Las
        </button>
      </div>
    </section>
  );
}

export default Filters;
