function Pages({handlePage, array, NUM_TASK, page}){

    const [pagesCount, setPagesCount] = useState(1)

    const changeCountPages = () => {
        setPagesCount(Math.ceil(array.length/NUM_TASK))
    }

    for (let i = 1; i<pagesCount+1; i++) {
        return (
        <div className="pagination__pages__number {(i===page)?('activePages'):('')}">{i}</div>
        )
    }




}

export default Pages
