import { useState } from 'react'

function Filters({tasksArray, tasksRender, filterAllArray,filterDoneArray,filterUnDoneArray,sortEarlyDate, sortLastDate}){    

    const [btnActive, setActiveBtn] = useState({ filter:'filter__undone', sort:'sort__last'} )

    console.log(btnActive);

    return(
        <section className="filterBox">
            <div className="filterBox__filter">
                <button onClick={() => {setActiveBtn(btnActive.filter='filter__all'); filterAllArray()}} className={btnActive.filter === 'filter__all' ? 'filter__all btnActive' : 'filter__all'} >All</button>
                <button className="filter__done" onClick={() => filterDoneArray(tasksArray)}>Done</button>
                <button className="filter__undone" onClick={() => filterUnDoneArray(tasksArray)}>Undone</button>
            </div>
            <div className="filterBox__sorting">
                <span>Sort by Date</span>
                <button className="sort__early" onClick={() => sortEarlyDate(tasksRender)}>Ear</button>
                <button className="sort__last btnActive"  onClick={() => sortLastDate(tasksRender)}>Las</button>
            </div>
        </section>
    )
}

export default Filters
