import Task from "./Task";


function TasksBox({NUM_TASK, page, tasks, setTasks, filtredArray}) {
    const removeTask = (id) => {
        setTasks(tasks.filter((task) => task.id !== id));
      };

      const checkTask = (id) => {
        setTasks(
          tasks.map((task) => ({
            ...task,
            check: task.id === id ? !task.check : task.check,
          }))
        );
      };


      const validate = (val) => {
        if (val === '') {
            alert('Введите что-нибудь');
            return false};
        let arrayWord = val.split(' ');
        let result = true;

        arrayWord.forEach(item => {
            if (item.length > 32) {
                alert('многого хотите, нет такого слова');
                result = false;}
        });

        return result;
    }



  return (
    <div className="tasksBox">
      {filtredArray
        .slice((page - 1) * NUM_TASK, NUM_TASK * page)
        .map((task) => {
          return (
            <Task
              task={task}
              key={task.id}
              removeTask={removeTask}
              checkTask={checkTask}
              tasks={tasks}
              page={page}
              validate={validate}
            />
          );
        })}
    </div>
  );
}

export default TasksBox;
